INSERT INTO safety_data (element_symbol, hazard_level, hazard_types, precautions, first_aid, storage, disposal, incompatible_with, emergency_procedures) VALUES
('H', 'high', ARRAY['flammable', 'explosive'], ARRAY['Store in well-ventilated area', 'Keep away from ignition sources', 'Use explosion-proof equipment', 'Ground all equipment'], '{"inhalation": "Move to fresh air immediately. If breathing is difficult, give oxygen.", "skinContact": "Generally not applicable for gas, but if liquid contact occurs, treat for frostbite.", "eyeContact": "Flush with water for 15 minutes if liquid contact occurs."}', 'Store in cool, dry, well-ventilated area away from incompatible materials.', 'Allow gas to escape slowly in well-ventilated area or burn off safely.', ARRAY['oxidizers', 'halogens', 'nitrous oxide'], ARRAY['Evacuate area if large leak occurs', 'Eliminate ignition sources', 'Use water spray to disperse vapors']),
('O', 'medium', ARRAY['oxidizer', 'supports combustion'], ARRAY['Keep away from combustible materials', 'Use only with compatible equipment', 'Ensure proper ventilation'], '{"inhalation": "Move to fresh air. High concentrations may cause oxygen toxicity.", "skinContact": "Generally safe, but liquid oxygen can cause frostbite.", "eyeContact": "Flush with water if liquid contact occurs."}', 'Store in cool, dry area away from combustible materials.', 'Allow gas to escape slowly in well-ventilated area.', ARRAY['flammable materials', 'organic compounds', 'oils'], ARRAY['Remove combustible materials from area', 'Increase ventilation', 'Monitor oxygen levels']),
('Na', 'extreme', ARRAY['highly reactive', 'corrosive', 'fire hazard'], ARRAY['Store under mineral oil', 'Never expose to water', 'Use dry inert atmosphere', 'Wear full protective equipment'], '{"skinContact": "Remove contaminated clothing. Flush with water for 15 minutes. Seek immediate medical attention.", "eyeContact": "Flush with water for 15 minutes. Seek immediate medical attention.", "ingestion": "Do not induce vomiting. Give water to drink. Seek immediate medical attention."}', 'Store under mineral oil in airtight containers in cool, dry place.', 'React with alcohol to form sodium alkoxide, then dispose as hazardous waste.', ARRAY['water', 'alcohols', 'acids', 'halogens'], ARRAY['Evacuate area', 'Use dry sand or special dry powder to extinguish fires', 'Never use water']),
('Cl', 'high', ARRAY['toxic gas', 'corrosive', 'oxidizer'], ARRAY['Use in fume hood', 'Wear respiratory protection', 'Ensure emergency shower nearby', 'Monitor gas levels'], '{"inhalation": "Move to fresh air immediately. If breathing is difficult, give oxygen. Seek medical attention.", "skinContact": "Remove contaminated clothing. Flush with water for 15 minutes.", "eyeContact": "Flush with water for 15 minutes. Seek immediate medical attention."}', 'Store in cool, dry, well-ventilated area away from incompatible materials.', 'Neutralize with sodium hydroxide solution before disposal.', ARRAY['ammonia', 'hydrogen', 'metals', 'organic compounds'], ARRAY['Evacuate area', 'Use water spray to absorb gas', 'Wear full protective equipment']);
